$(document).ready(function () {
  $('#hamburger').click(function () {
    $('#hamburger').addClass('hidden');
    $('#close').removeClass('hidden');
    $('#navigation').removeClass('hidden-xs');
    $('#navigation').removeClass('hidden-sm');
  });
  $('#close').click(function () {
    $('#hamburger').removeClass('hidden');
    $('#navigation').addClass('hidden-xs');
    $('#navigation').addClass('hidden-sm');
  });

  $("#IE").click(function(){
    $(".details").show();
    $("#IE").hide();
  });
  $("#IE-close").click(function(){
    $(".details").hide();
    $("#IE").show();
  });

  /* Création des barres de compétences + animations */
  $('.barres li').each(function() {
    var pourcentage = $(this).attr('data-skills');
    $(this).append($("<span></span>").animate({
      width : ''+ pourcentage +'%' }, 2000));
  });

});

